      $(\"#input\" ).on('keypress',function(e) {
        if(e.which == 13) {
            const inp = document.querySelector(\"#input\");
            inp.value = inp.value.trim();
            executedata('ajaxRequest')
        }
    })

    $('body').on('paste', 'input, textarea', function (e)
    {
        setTimeout(function ()
        {
            const inp = document.querySelector(\"#input\");
            inp.value = inp.value.trim();
            // executedata('ajaxRequest')
        },0);
    });

    document.addEventListener('textInput', function (e){
        hapus = ''
        if(e.data.length >= 8){
            // console.log('IR scan textInput', e.data);
            $('#input').val(hapus);
                const inp = document.querySelector(\"#input\");
                inp.value = inp.value.trim();
            setTimeout(function ()
            {
                executedata('ajaxRequest')

            },100);
            
        }
    });


   

");

$this->registerJs("

function executedata(){
    var input = $('#input').val();
    $( '.invoice-col' ).remove();
    $( '.tableini' ).remove();
    $.ajax({
        type: 'POST',
        url: 'api/itemcodesearch',
        cache: false,
        method:'POST',
        data:{data:input},
        success: function(response) {
            if (response.datanya == 'kosong') {
                var data = '<p>Data Tidak Ditemukan</p>'
                $('#header').html(data);
            } else {
                $('#header').html(response.header);
                $('.tablenya').html(response.datanya);
                $('#input').val('');
            }
        },
        failure: function (response) {
            Swal.fire(
            'Internal Error',
            'Oops, your Data was not Found.', // had a missing comma
            'error'
            )
        }
    });
}



",View::POS_END);


